var _ = require('lodash');
function urlJoin(a, b) {
  return _.trimEnd(a, '/') + '/' + _.trimStart(b, '/');
}

pm.test("checks vulnerability with relative path", function () {
    const response = cheerio.load(pm.response.text(), {
        ignoreWhitespace: true,
        xmlMode: true
    });

    const imageTags = Object.values(response("img"));
    let vulnerableResource = "";
    for (var i = 0; i < imageTags.length; i++) {
        if (imageTags[i].type === "tag" && imageTags[i].name === "img") {
            const src = imageTags[i].attribs.src;
            if (src && src.indexOf("cdn") === -1 && src.indexOf("?") !== -1) {
                vulnerableResource = src;
                break;
            }
        }
    }
    vulnerableResource = vulnerableResource.substr(0, vulnerableResource.lastIndexOf("=") + 1)

    const pathsToCheck = [
        { path: '../../../etc/passwd', type: 'Linux passwd file' },
        { path: '../../../etc/shadow', type: 'Linux shadow file' },
        { path: '../../../etc/hosts', type: 'Linux hosts file' },
        { path: '../../../etc/profile', type: 'Linux profile file' },
        { path: '../../../etc/apache2/httpd.conf', type: 'Apache httpd.conf file' },
        { path: '../../../windows/win.ini', type: 'Windows win.ini file' },
        { path: '../../../../../windows/system.ini', type: 'Windows system.ini file' },
        { path: '../../../var/www/html/admin/get.inc', type: 'Web server include file' },
        { path: '../../../var/www/html/get.php', type: 'PHP file' }
    ];

    let vulnerabilityFound = false;
    let vulnerabilityType = "";

    for(var i=0; i<pathsToCheck.length; i++){
        const { path, type } = pathsToCheck[i];
        const vulnerable_url = urlJoin(urlJoin("/api/v3",vulnerableResource), path);

        pm.sendRequest(vulnerable_url, function (err, value) {
            if (value) {
                pm.expect(value.code).to.not.equal(200);
                if(value.code === "200"){
                    vulnerabilityFound = true;
                    vulnerabilityType = type;
                }
            }
        });
    }

    pm.test("Vulnerability check", function () {
        pm.expect(vulnerabilityFound).to.be.false;
        if (vulnerabilityFound) {
            pm.assert.fail(`Vulnerability found: ${vulnerabilityType}`);
        } else {
            pm.expect(true).to.be.true; // Dummy assertion to ensure test passes if no vulnerability is found
        }
    });
});

